$(document).ready(function(){function populateSpecialtiesDropdown(){var $s=$('select.select-dropdown-specialties'),$n=$('div.nice-select-specialty ul.list'),a={},o=[];$('.specialties-list-to-dropdown').each(function(){var s=$(this).text().trim();if(!a[s]){a[s]=true;o.push($('<option>',{value:s,text:s}));$n.append($('<li>',{'data-value':s,'class':'option',text:s}));}});$s.append(o);$('select.select-dropdown-specialties').niceSelect('update');}function populateDoctorsDropdown(s){var $d=$('select.select-dropdown-doctors'),$n=$('div.nice-select-doctors ul.list'),o=[];$d.empty();$n.empty();$d.append($('<option>',{value:'',text:'Choose Doctor'}));$n.append($('<li>',{'data-value':'','class':'option',text:'Choose Doctor'}));$('.doctors-list-to-dropdown').each(function(){var d=$(this).text().trim(),sp=$(this).siblings('.specialties-list-to-dropdown').text().trim();if(s===''||sp===s){o.push($('<option>',{value:d,text:d}));$n.append($('<li>',{'data-value':d,'class':'option',text:d}));}});$d.append(o);$('select.select-dropdown-doctors').niceSelect('update');var $nd=$('div.nice-select-doctors');$nd.find('span.current').text('Choose Doctor');$nd.find('.option.selected').removeClass('selected');$nd.find('.option[data-value=""]').addClass('selected');}function showDoctorDetails(d){$('.w-dyn-item').each(function(){var $t=$(this),n=$t.find('.doctors-list-to-dropdown').text().trim();if(n===d)$t.removeClass('d-none');else $t.addClass('d-none');});}$('select').niceSelect();populateSpecialtiesDropdown();populateDoctorsDropdown('');$('#Specialty').on('change',function(){var s=$(this).val();populateDoctorsDropdown(s);$('#Doctor').val('').niceSelect('update');showDoctorDetails('');});$('#Doctor').on('change',function(){showDoctorDetails($(this).val());});});
